var Dataprovider = require('./dataprovider'),
    Fieldmodel = require('./data/fieldmodel');

function Prolob () {

    this.fieldModels = {};
    this.tableModels = {};
    this.viewModels = {};

}

Prolob.compile = function compile (fieldname,field) {
    "use strict";

    field.fieldName = fieldname;

    return field;
};






Prolob.prototype.addFieldModel = function(fieldname,field){
    "use strict";

    if(!this.fieldModels[fieldname]){

        var model = Prolob.compile(fieldname,field);


       // model.init();
        this.fieldModels[fieldname] = model;
    }


};




//Prolob.prototype.dataprovider = Dataprovider;


module.exports = exports = new Prolob;
var prolob = module.exports;


prolob.Fieldmodel = Fieldmodel;
prolob.Dataprovider = Dataprovider;
